{-# LANGUAGE OverloadedStrings #-}

module TestData where

import Data.ByteString
import qualified Data.ByteString.Base16 as B16 (encode, decode)

import Network.CryptoNote.Transaction as Transaction

txTestDataHex 0 = ("08e5f8d6ac30b35356548f9dd5bb477d508fff2c7ac8580856495d4e7527e557",
                   "",
                   Transaction { prefix = TransactionPrefix { version = 0
                                                            , unlockTime = 0
                                                            , inputs = []
                                                            , outputs = []
                                                            , extra = []
                                                            }
                               , signatures = []
                               })


txIdBytes :: Int -> ByteString
txIdBytes n = result
            where (result, _, _) = txTestData n

txBodyBytes :: Int -> ByteString
txBodyBytes n = result
            where (_, result, _) = txTestData n

txBodyEntity :: Int -> Transaction
txBodyEntity n = result
            where (_, _, result) = txTestData n


txTestData x = (decodeB16OrFail txIdHex, decodeB16OrFail txHex, txEntity)
             where (txIdHex, txHex, txEntity) = txTestDataHex x

decodeB16OrFail hex = case B16.decode hex of
                        (bytes, "") -> bytes
